import React, { Component } from 'react';


export default class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            email: "",
            text: ""
        }
    }

    handleSubmit = e => {
        e.preventDefault();
        const data = [
            {name: this.state.name},
            {email: this.state.email},
            {text: this.state.text}
        ]
        const mySpreadSheet = "https://docs.google.com/spreadsheets/d/1HxTJrDrdErdQC5ctSgiU8LBoQHfVioxO-EWk-4PHZLU/edit#gid=0";

        fetch(mySpreadSheet, { method: 'POST', body: JSON.stringify(data)})
      .then(response => console.log('Success!', response))
      .catch(error => console.error('Error!', error.message))

    }
    
    render() {
        return (
            <form className="main-content_contact_content_form" onSubmit={this.handleSubmit}>
                <input type="text" onChange={e => this.setState({name: e.target.value})} placeholder="enter name"/>
                <input type="email" onChange={e => this.setState({email: e.target.value})} placeholder="enter e-mail" />
                <textarea value={this.state.text} onChange={e => this.setState({text: e.target.value})} />
                <input type="submit" value="Send" />
            </form>
        )
    }
}
