import React, { Component } from 'react';

class Header extends Component {

    constructor(props) {
        super(props);
        this.state = {
            myPortf: ""
        }
    }

    componentDidMount() {
        this.toCompleteWord = () => {
            const word = "My portfolio";
            for(let i = 0; i < word.length; i++) {
                setInterval(() => {
                    this.setState({
                        myPortf: this.state.myPortf + word[0]
                    })
                }, 1000)

            }
        }
        this.toCompleteWord()
    }
    
    render() {
        return (
            <header className="header-wrapper">
                <div className="header-wrapper_logo">
                    <p>{this.state.myPortf}</p>
                </div>
                <nav className="header-wrapper_nav">
                    <ul className="header-wrapper_nav_list">
                        <li><a>About me</a></li>
                        <li><a>My projects</a></li>
                        <li><a>Contact</a></li>
                    </ul>
                </nav>
            </header>
        );
    }
}

export default Header;
