const {GoogleSpreadsheet} = require("google-spreadsheet");
// const {promisify} = require("util");
const doc = new GoogleSpreadsheet("1HxTJrDrdErdQC5ctSgiU8LBoQHfVioxO-EWk-4PHZLU");
// const creds = require("./creds-from-google.json");

async function accessSpreadsheet() {
    // await doc.useServiceAccountAuth(require('./creds-from-google.json'));
    // OR use API key -- only for read-only access to public sheets
    doc.useApiKey('AIzaSyCrIDjXL5DMoTxvAoK65ulfZS1zXftH6ZU');

    await doc.loadInfo(); // loads document properties and worksheets
    console.log(doc.name);
    await doc.updateProperties({ title: 'renamed doc' });

    const sheet = doc.sheetsByIndex[0]; // or use doc.sheetsById[id]
    // console.log(sheet.title);
    // console.log(sheet.rowCount);
}

// accessSpreadsheet();

export default accessSpreadsheet;
