import React, {useState, useEffect} from 'react'

export default function AboutMe() {

    const [width1, setWidth1] = useState(3);
    const [width2, setWidth2] = useState(5);
    const [width3, setWidth3] = useState(4);
    const [rotate1, setRotate1] = useState(0);
    const [rotate2, setRotate2] = useState(40);
    const [rotate3, setRotate3] = useState(100);
    const [distance1, setDistance1] = useState(2.5);
    const [distance2, setDistance2] = useState(4.5);
    const [distance3, setDistance3] = useState(3.5);
    const [counter, setCounter] = useState(0);

    const changeWidths = () => {
        setCounter(counter+1);
        const width1Change = Math.floor(Math.random() * (Math.floor(15) - Math.ceil(1) + 1)) + Math.ceil(1);
        const width2Change = Math.floor(Math.random() * (Math.floor(15) - Math.ceil(1) + 1)) + Math.ceil(1);
        const width3Change = Math.floor(Math.random() * (Math.floor(15) - Math.ceil(1) + 1)) + Math.ceil(1);
        setWidth1(width1Change);
        setWidth2(width2Change);
        setWidth3(width3Change);
    }

    const changeRotates = () => {
        const rotate1Change = Math.floor(Math.random() * ((Math.floor(36) - Math.ceil(-36) + 1)) + Math.ceil(-36))*10;
        const rotate2Change = Math.floor(Math.random() * ((Math.floor(36) - Math.ceil(-36) + 1)) + Math.ceil(-36))*10;
        const rotate3Change = Math.floor(Math.random() * ((Math.floor(36) - Math.ceil(-36) + 1)) + Math.ceil(-36))*10;
        setRotate1(rotate1Change);
        setRotate2(rotate2Change);
        setRotate3(rotate3Change);
    }

    const changeDistnances = () => {
        setDistance1(width1-0.5);
        setDistance2(width2-0.5);
        setDistance3(width3-0.5);
    }

    const interval = () => {
        setInterval(changeValues, 5000);
        clearInterval(this);
    }

    if(counter === 0) {
        // interval()
    }
    if(counter !==0) {
        clearInterval(interval)
        setCounter(0);
    }
    
    return (
        <section className="about-me">
            <div className="about-me__circle">
                <div className="about-me__circle__line" style={{width: `${width1}rem`, transform: `rotate(${rotate1}deg)`}}>
                    <div className="about-me__circle__line-circle" style={{left: `${distance1}rem`}}></div>
                </div>
                <div className="about-me__circle__line" style={{width: `${width2}rem`, transform: `rotate(${rotate2}deg)`}}>
                    <div className="about-me__circle__line-circle" style={{left: `${distance2}rem`}}></div>
                </div>
                <div className="about-me__circle__line" style={{width: `${width3}rem`, transform: `rotate(${rotate3}deg)`}}>
                    <div className="about-me__circle__line-circle" style={{left: `${distance3}rem`}}></div>
                </div>
            </div>
        </section>
    )
}
