import React, { Component } from 'react';
import photo from "../photos/cup-of-coffee-1280537_1920.jpg";

export default class AboutMe extends Component {
    constructor(props) {
        super(props);
        this.state = {
            opacityClass: ""
        }
    }

    componentDidMount() {
        this.timer = setTimeout(() => {
            this.setState({
                opacityClass: "active-opacity"
            })
        }, 100)
    }

    // componentDidMount() {
    //     this.setState({
    //         opacityClass: "active-opacity"
    //     })
    // }
    
    render() {
        return (
            <section className={`main-content_about-me ${this.state.opacityClass}`}>
                <img src={photo} alt="cup of coffee and laptop" />
                <div className="main-content_about-me_wrapper">
                    <h2>
                        About me
                    </h2>
                    <p>
                    Hi, my name is Kamil Wojtas. I am a front-end programmer. I catch up on my short experience in programming by willingness, enthusiasm and quick learning. I like to gain new experiences and I am open to various options. Technologies in which I feel strong:
                    React,
                    Redux
                    HTML 5
                    CSS 3 (And also Sass / SCSS),
                    Java Script ES6.
                    </p>
                </div>
            </section>
        )
    }
}
