import React, {useState, useEffect} from 'react'

export default function Contact() {
    
    const [introClass, setIntroClass] = useState("contact__form--intro");

    const changeClass = () => {
        setIntroClass("")
    }
    useEffect(() => {
        setTimeout(changeClass, 600);
    })


    return (
        <section className="contact">
            <form className={`contact__form ${introClass}`}>
                <input className="contact__form__input" placeholder="Enter your name" />
                <input className="contact__form__input" placeholder="Enter your e-mail" />
                <textarea className="contact__form__input contact__form__input--text" placeholder="Enter your message" />
                <input type="button" className="contact__form__input contact__form__input--btn" value="Send" />
            </form>
        </section>
    )
}
