import React, { Component } from 'react';


export default class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            email: "",
            text: ""
        }
    }

    handleSubmit = e => {
        e.preventDefault();
        const { GoogleSpreadsheet } = require('google-spreadsheet');
        const doc = new GoogleSpreadsheet('1HxTJrDrdErdQC5ctSgiU8LBoQHfVioxO-EWk-4PHZLU');
    }
    
    render() {
        return (
            <form className="main-content_contact_content_form" onSubmit={this.handleSubmit}>
                <input type="text" onChange={e => this.setState({name: e.target.value})} placeholder="enter name"/>
                <input type="email" onChange={e => this.setState({email: e.target.value})} placeholder="enter e-mail" />
                <textarea value={this.state.text} onChange={e => this.setState({text: e.target.value})} />
                <input type="submit" value="Send" />
            </form>
        )
    }
}
